mapper.delete:
delete from  pas_pro_todo t

     and t.pro_instance=#{proInstance}   
     and t.act_id=#{actId}

mapper.select:
select
t.pro_instance "proInstance",
t.act_id "actId",
t.act_name "actName",
t.pro_def_key "proDefKey",
t.pro_def_name "proDefName",
t.auditor_id "auditorId",
t.auditor_name "auditorName",
t.task_id "taskId"
from pas_pro_todo t

     and t.pro_instance=#{proInstance} 
     and t.act_id=#{actId} 
     and t.act_name=#{actName} 
     and t.pro_def_key=#{proDefKey} 
     and t.pro_def_name=#{proDefName} 
     and t.auditor_id=#{auditorId} 
     and t.auditor_name=#{auditorName} 
     and t.task_id=#{taskId}
